const API_URL = 'http://localhost:5000/api/alumnos';

export const addAlumno = async (alumno, fotos) => {
  const formData = new FormData();
  formData.append('uid', alumno.uid);
  formData.append('nombre', alumno.nombre);
  formData.append('numeroBoleta', alumno.numeroBoleta);
  formData.append('numeroPlaca', alumno.numeroPlaca);
  if (fotos.fotoPerfil) formData.append('fotoPerfil', fotos.fotoPerfil);
  if (fotos.fotoVehiculo) formData.append('fotoVehiculo', fotos.fotoVehiculo);

  const response = await fetch(API_URL, {
    method: 'POST',
    body: formData,
  });

  if (!response.ok) throw new Error('Error al registrar el alumno');
  return response.json();
};

export const getAllAlumnos = async () => {
  const response = await fetch(API_URL);
  if (!response.ok) throw new Error('Error al obtener los alumnos');
  return response.json();
};
