import React, { useState } from "react";
import "./styles.css";

export default function AdminDashboard() {
  const [formData, setFormData] = useState({
    uid: "",
    nombre: "",
    numeroPlaca: "",
    fotoPerfil: null,
    fotoVehiculo: null,
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleFileChange = (e) => {
    const { name, files } = e.target;
    setFormData((prev) => ({ ...prev, [name]: files[0] }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Datos a registrar:", formData);
    // Aquí puedes hacer una petición al backend para almacenar la info
  };

  return (
    <div className="admin-container">
      <h1>Bienvenido, Administrador</h1>
      <p>Registra un nuevo alumno en la base de datos.</p>
      <form onSubmit={handleSubmit} className="admin-form">
        <label>
          UID del Alumno:
          <input type="text" name="uid" value={formData.uid} onChange={handleChange} required />
        </label>
        <label>
          Nombre del Alumno:
          <input type="text" name="nombre" value={formData.nombre} onChange={handleChange} required />
        </label>
        <label>
          Número de Placa:
          <input type="text" name="numeroPlaca" value={formData.numeroPlaca} onChange={handleChange} required />
        </label>
        <label>
          Foto de Perfil:
          <input type="file" name="fotoPerfil" accept="image/*" onChange={handleFileChange} required />
        </label>
        <label>
          Foto del Vehículo:
          <input type="file" name="fotoVehiculo" accept="image/*" onChange={handleFileChange} required />
        </label>
        <button type="submit">Registrar Alumno</button>
      </form>
    </div>
  );
}
