import React, { useEffect, useState } from "react";
import "./styles.css";
import { getAlumno } from "../database/db"; // Asegúrate de importar la función correcta

export default function AlumnoDashboard({ studentUID }) {
  const [bgGradient, setBgGradient] = useState("");
  const [studentData, setStudentData] = useState(null);

  useEffect(() => {
    // Generar colores aleatorios para el fondo
    const randomColor1 = `#${Math.floor(Math.random() * 16777215).toString(16)}`;
    const randomColor2 = `#${Math.floor(Math.random() * 16777215).toString(16)}`;
    setBgGradient(`linear-gradient(135deg, ${randomColor1}, ${randomColor2})`);
  }, []);

  useEffect(() => {
    if (studentUID) {
      getAlumno(studentUID).then((data) => {
        if (data) {
          setStudentData(data);
        } else {
          console.log("No se encontró el alumno en la base de datos.");
        }
      });
    }
  }, [studentUID]);

  if (!studentData) {
    return <h2>Cargando información del alumno...</h2>;
  }

  return (
    <div className="alumno-container" style={{ background: bgGradient }}>
      <div className="student-card">
        <div className="profile-section">
          <img src={studentData.fotoAlumno || "defaultProfilePic.jpg"} alt="Foto de perfil" className="profile-pic" />
        </div>
        <div className="info-section">
          <h2 className="student-name">{studentData.nombre}</h2>
          <p className="student-id">Boleta: {studentData.boleta}</p>
        </div>
        <div className="qr-section">
          <img src={studentData.fotoVehiculo || "defaultQR.jpg"} alt="Código QR" className="qr-code" />
        </div>
      </div>
    </div>
  );
}
