import React from 'react';
import { FcGoogle } from "react-icons/fc";

function LoginScreen({ onLogin }) {
    return (
        <div className="flex flex-col items-center justify-center h-screen bg-white p-6">
          <div className="w-full max-w-sm bg-white shadow-md rounded-2xl p-6">
            <h1 className="text-3xl font-semibold text-center text-gray-800">¡Bienvenido!</h1>
            <p className="text-center text-gray-500 mb-6">Inicia sesión para continuar</p>
            <button
              onClick={onLogin}
              className="w-full flex items-center justify-center space-x-2 bg-gray-100 text-gray-800 font-medium px-4 py-3 rounded-lg shadow-md hover:bg-gray-200 transition duration-300"
            >
              <FcGoogle className="text-2xl" />
              <span>Iniciar sesión con Google</span>
            </button>
          </div>
        </div>
      );
}

export default LoginScreen;
