import React, { useState, useEffect } from "react";
import { addAlumno, getAllAlumnos } from "../db";

export default function AdminDashboard() {
  const [formData, setFormData] = useState({
    uid: "",
    nombre: "",
    numeroBoleta: "",
    fotoPerfil: "",
    numeroPlaca: "",
    fotoVehiculo: ""
  });

  const [alumnos, setAlumnos] = useState([]);

  useEffect(() => {
    // Cargar alumnos al inicio
    async function fetchAlumnos() {
      const data = await getAllAlumnos();
      setAlumnos(data);
    }
    fetchAlumnos();
  }, []);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!formData.uid) {
      alert("El UID es obligatorio.");
      return;
    }

    try {
      await addAlumno(formData);
      alert("Alumno registrado exitosamente");
      setFormData({ uid: "", nombre: "", numeroBoleta: "", fotoPerfil: "", numeroPlaca: "", fotoVehiculo: "" });

      // Actualizar lista de alumnos
      const data = await getAllAlumnos();
      setAlumnos(data);
    } catch (error) {
      alert("Error al registrar el alumno");
    }
  };

  return (
    <div className="admin-container">
      <h1>Panel de Administración</h1>
      <form onSubmit={handleSubmit} className="admin-card">
        <input type="text" name="uid" placeholder="UID del Alumno" value={formData.uid} onChange={handleChange} required />
        <input type="text" name="nombre" placeholder="Nombre Completo" value={formData.nombre} onChange={handleChange} required />
        <input type="text" name="numeroBoleta" placeholder="Número de Boleta" value={formData.numeroBoleta} onChange={handleChange} required />
        <input type="text" name="fotoPerfil" placeholder="URL de Foto de Perfil" value={formData.fotoPerfil} onChange={handleChange} required />
        <input type="text" name="numeroPlaca" placeholder="Número de Placa" value={formData.numeroPlaca} onChange={handleChange} required />
        <input type="text" name="fotoVehiculo" placeholder="URL de Foto del Vehículo" value={formData.fotoVehiculo} onChange={handleChange} required />
        <button type="submit" className="admin-button">Registrar</button>
      </form>

      <h2>Alumnos Registrados</h2>
      <ul>
        {alumnos.map((alumno) => (
          <li key={alumno.uid}>
            {alumno.nombre} - {alumno.numeroBoleta}
          </li>
        ))}
      </ul>
    </div>
  );
}
