import React, { useEffect, useState } from "react";
import "./styles.css";
import profilePic from "./profilePic.jpg";
import qrCode from "./qr.png";

export default function AlumnoDashboard({ uid }) {
  const [bgGradient, setBgGradient] = useState("");
  const [student, setStudent] = useState(null);

  useEffect(() => {
    const randomColor1 = `#${Math.floor(Math.random() * 16777215).toString(16)}`;
    const randomColor2 = `#${Math.floor(Math.random() * 16777215).toString(16)}`;
    setBgGradient(`linear-gradient(135deg, ${randomColor1}, ${randomColor2})`);

    console.log(uid); 

    if (uid) {
      getAlumno(uid).then((data) => {
        //console.log("Informacion obtenida: ", data);
        if (data) {
          setStudent(data);
        }
      });
    }
  }, [uid]);

  return (
    <div className="alumno-container" style={{ background: bgGradient }}>
      <div className="student-card">
        <div className="profile-section">
          <img src={student?.profilePic || profilePic} alt="Foto de perfil" className="profile-pic" />
        </div>
        <div className="info-section">
          <h2 className="student-name">{student?.name || "Nombre no disponible"}</h2>
          <p className="student-id">Boleta: {student?.studentId || "Sin boleta"}</p>
        </div>
        <div className="qr-section">
          <img src={qrCode} alt="CÃ³digo QR" className="qr-code" />
        </div>
      </div>
    </div>
  );
}
