import { useState, useEffect } from "react";
import axios from "axios";

export default function AlumnoDashboard() {
  const [alumno, setAlumno] = useState(null);
  const [uid, setUid] = useState("");
  const [error, setError] = useState(null);

  const handleFetchAlumno = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/alumnos/${uid}`);
      setAlumno(response.data);
      setError(null);
    } catch (err) {
      setError("Alumno no encontrado");
      setAlumno(null);
    }
  };

  return (
    <div className="p-5">
      <h1 className="text-2xl font-bold">Alumno Dashboard</h1>
      <input
        type="text"
        placeholder="Ingrese su UID"
        value={uid}
        onChange={(e) => setUid(e.target.value)}
        className="block border p-2 mt-2"
      />
      <button onClick={handleFetchAlumno} className="bg-blue-500 text-white p-2 mt-2 rounded">
        Buscar Alumno
      </button>
      {error && <p className="text-red-500 mt-2">{error}</p>}
      {alumno && (
        <div className="border p-4 mt-4">
          <h2 className="text-xl font-bold">Información del Alumno</h2>
          <p><strong>Nombre:</strong> {alumno.nombre}</p>
          <p><strong>Boleta:</strong> {alumno.boleta}</p>
          <p><strong>Matrícula:</strong> {alumno.matriculaCarro}</p>
          <img src={`http://localhost:5000/uploads/${alumno.fotoAlumno}`} alt="Foto del Alumno" className="w-20 h-20 mt-2" />
          <img src={`http://localhost:5000/uploads/${alumno.fotoVehiculo}`} alt="Foto del Vehículo" className="w-20 h-20 mt-2" />
        </div>
      )}
    </div>
  );
}
