import { useState, useEffect } from "react";
import axios from "axios";
import "../components/styles.css"

export default function AdminDashboard() {
  const [alumnos, setAlumnos] = useState([]);
  const [formData, setFormData] = useState({
    uid: "",
    nombre: "",
    boleta: "",
    matriculaCarro: "",
    fotoVehiculo: null,
    fotoAlumno: null,
  });

  // useEffect(() => {
  //   axios.get("http://localhost:5000/alumnos").then((response) => {
  //     setAlumnos(response.data);
  //   });
  // }, []);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleFileChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.files[0] });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const data = new FormData();

    // Convertir valores de texto a string explícitamente
    Object.keys(formData).forEach((key) => {
      if (key === "fotoVehiculo" || key === "fotoAlumno") {
        data.append(key, formData[key]);
      } else {
        data.append(key, String(formData[key]));
      }
    });

    try {
      const response = await axios.post("http://localhost:5000/registro", data, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      alert("Alumno registrado");
    } catch (error) {
      console.error("Error en la solicitud:", error.response);
    }
  };

  return (
    <div className="admin-container">
      <h1>Admin Dashboard</h1>
      <form id="alumnoform">
        <input type="text" name="uid" placeholder="UID" required className="admin-input" />
        <input type="text" name="nombre" placeholder="Nombre" required className="admin-input" />
        <input type="text" name="boleta" placeholder="Boleta" required className="admin-input" />
        <input type="text" name="matriculaCarro" placeholder="Matrícula" required className="admin-input" />

        <label htmlFor="fotoVehiculo" className="upload-label">Subir foto del vehículo</label>
        <input type="file" id="fotoVehiculo" name="fotoVehiculo" required className="file-input" />

        <label htmlFor="fotoAlumno" className="upload-label">Subir foto del alumno</label>
        <input type="file" id="fotoAlumno" name="fotoAlumno" required className="file-input" />

        <button type="submit" className="admin-button">Registrar Alumno</button>
      </form>
      <h2>Lista de Alumnos</h2>
      <ul id="alumnoLista" className="admin-list"></ul>
    </div>
  );  
}
