import { useState, useEffect } from "react";
import axios from "axios";

export default function AdminDashboard() {
  const [alumnos, setAlumnos] = useState([]);
  const [formData, setFormData] = useState({
    uid: "",
    nombre: "",
    boleta: "",
    matriculaCarro: "",
    fotoVehiculo: null,
    fotoAlumno: null,
  });

  // useEffect(() => {
  //   axios.get("http://localhost:5000/alumnos").then((response) => {
  //     setAlumnos(response.data);
  //   });
  // }, []);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleFileChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.files[0] });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const data = new FormData();

    // Convertir valores de texto a string explícitamente
    Object.keys(formData).forEach((key) => {
      if (key === "fotoVehiculo" || key === "fotoAlumno") {
        data.append(key, formData[key]);
      } else {
        data.append(key, String(formData[key]));
      }
    });

    try {
      const response = await axios.post("http://localhost:5000/registro", data, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      alert("Alumno registrado");
    } catch (error) {
      console.error("Error en la solicitud:", error.response);
    }
  };

  return (
    <div className="max-w-3xl mx-auto p-8 bg-white shadow-xl rounded-lg mt-10 border border-gray-200">
      <h1 className="text-4xl font-extrabold text-gray-900 text-center mb-8">Admin Dashboard</h1>
      <form onSubmit={handleSubmit} className="grid grid-cols-2 gap-4">
        <input type="text" name="uid" placeholder="UID" onChange={handleChange} required className="w-full border p-3 rounded-lg focus:ring-2 focus:ring-blue-400" />
        <input type="text" name="nombre" placeholder="Nombre" onChange={handleChange} required className="w-full border p-3 rounded-lg focus:ring-2 focus:ring-blue-400" />
        <input type="text" name="boleta" placeholder="Boleta" onChange={handleChange} required className="w-full border p-3 rounded-lg focus:ring-2 focus:ring-blue-400" />
        <input type="text" name="matriculaCarro" placeholder="Matrícula" onChange={handleChange} required className="w-full border p-3 rounded-lg focus:ring-2 focus:ring-blue-400" />
        <input type="file" name="fotoVehiculo" onChange={handleFileChange} required className="w-full border p-3 rounded-lg bg-gray-50" />
        <input type="file" name="fotoAlumno" onChange={handleFileChange} required className="w-full border p-3 rounded-lg bg-gray-50" />
        <div className="col-span-2">
          <button type="submit" className="w-full bg-blue-600 text-white p-3 rounded-lg font-bold hover:bg-blue-700 transition duration-300">Registrar Alumno</button>
        </div>
      </form>

      <h2 className="text-2xl font-semibold text-gray-900 mt-8">Lista de Alumnos</h2>
      <ul className="mt-4 space-y-3">
        {alumnos.map((alumno, index) => (
          <li key={index} className="border p-4 rounded-lg bg-blue-50 flex justify-between items-center shadow-md">
            <span className="font-medium text-gray-800">{alumno.nombre} - {alumno.boleta}</span>
          </li>
        ))}
      </ul>
    </div>
  );
}
